Ext.define('TMF14.controller.Day4', {
    extend: 'Ext.app.Controller',

    config: {
        routes: {
            'Day4': 'showDay4'
        },

        control: {
            "list#Day4": {
                activate: 'onDay4Activate',
                initialize: 'onDay4Initialize',
                hide: 'onDay4Hide',
                itemtap: 'onDay4ItemTap'
            }
        }
    },

    onDay4Activate: function(newActiveItem, container, oldActiveItem, eOpts) {
        Ext.ComponentQuery.query('#sundayListIndicator')[0].show({type: 'fade'});
    },

    onDay4Initialize: function(component, eOpts) {
        TMF14.app.setDayIndicator(component, '#sundayListIndicator');
    },

    onDay4Hide: function(component, eOpts) {
        setTimeout(function() {
            component.destroy();
        }, 500);
    },

    onDay4ItemTap: function(dataview, index, target, record, e, eOpts) {
        var main = Ext.getCmp('main'),
            backButton = Ext.ComponentQuery.query('#backButton')[0];

        backButton.dayNumber = 'Day4';

        main.artistInfo = record.data;
        this.redirectTo('artistDetail');
    },

    showDay4: function() {
        var main = Ext.getCmp('main'),
            day = Ext.create('TMF14.view.Day4');

        main.setActiveItem(day);
    }

});